/* eslint-disable jsx-a11y/alt-text */
import React, { Component } from 'react';
import './index.scss';
import NavigateNextIcon from '@material-ui/icons/NavigateNext';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
class NavListOption extends Component {
	state = {
		navOptions: [
			{
				label: 'Dashboard Home',
				imgUrl: ''
			},
			{
				label: 'Lead Panel',
				imgUrl: '',
				children: [
					{
						label: 'Claimed Leads',
						imgUrl: ''
					},
					{
						label: 'Converted Leads',
						imgUrl: ''
					},
					{
						label: 'New Leads',
						imgUrl: ''
					}
				]
			},
			{
				label: 'Clinets',
				imgUrl: ''
			}
		]
	};
	handleNavState = subLevelObj => () => {
		this.setState(prevState => ({ [subLevelObj.label]: !prevState[subLevelObj.label] }));
	};
	handleNavList = children => {
		return children.map((child, index) => {
			if (!child.children) {
				return (
					<div className='nav-main-container'>
						{child.imgUrl !== '' ? <img src={''} /> : null}
						<div className='nav-main-label'>{child.label}</div>
					</div>
				);
			}
			return (
				<>
					<div className='nav-main-container-with-child' onClick={this.handleNavState(child)}>
						<div className='nav-main-block'>
							{child.imgUrl !== '' ? <img src={''} /> : null}
							<div className='nav-main-label'>{child.label}</div>
						</div>
						{this.state[child.label] ? <ExpandMoreIcon /> : <NavigateNextIcon />}
					</div>
					{this.state[child.label] ? this.handleNavList(child.children) : null}
				</>
			);
		});
	};

	render() {
		return <div className='nav-list-container'>{this.handleNavList(this.state.navOptions)}</div>;
	}
}

export default NavListOption;
