/* eslint-disable jsx-a11y/alt-text */
import React, { Component } from 'react';
import './index.scss';
import NavigateNextIcon from '@material-ui/icons/NavigateNext';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import HomeIcon from '@material-ui/icons/Home';
import ExtensionIcon from '@material-ui/icons/Extension';

class NavListOption extends Component {
	state = {
		navOptions: [
			{
				label: 'Dashboard Home',
				imgUrl: ''
			},
			{
				label: 'Lead Panel',
				imgUrl: '',
				childList: ['Claimed Leads', 'Converted Leads', 'New Leads'],
				children: [
					{
						label: 'Claimed Leads',
						imgUrl: ''
					},
					{
						label: 'Converted Leads',
						imgUrl: ''
					},
					{
						label: 'New Leads',
						imgUrl: ''
					}
				]
			},
			{
				label: 'Clinets',
				imgUrl: '',
				children: []
			},
			{
				label: 'My Space',
				imgUrl: '',
				children: []
			},
			{
				label: 'My Analytics',
				imgUrl: '',
				children: []
			},
			{
				label: 'Proposals',
				imgUrl: '',
				children: []
			},
			{
				label: 'Space Diagram',
				imgUrl: '',
				children: []
			},
			{
				label: 'Payments',
				imgUrl: '',
				children: []
			},
			{
				label: 'My Ads',
				imgUrl: '',
				children: []
			}
		],
		selectedNavItem: ''
	};
	handleNavState = subLevelObj => () => {
		let navitem = '';
		if (this.state.selectedNavItem === subLevelObj.label) {
			navitem = '';
		} else {
			navitem = subLevelObj.label;
		}
		this.setState({ selectedNavItem: navitem });
	};
	handleNavList = children => {
		return (
			children.length !== 0 &&
			children.map((child, index) => {
				if (!child.children) {
					return (
						<div
							className={
								this.state.selectedNavItem === child.label
									? 'nav-main-container highlight'
									: 'nav-main-container'
							}
							onClick={this.handleNavState(child)}
						>
							{child.imgUrl === '' ? (
								//<img src={child.imgUrl} className='nav-img' />
								<HomeIcon className='nav-img' />
							) : (
								<div style={{ width: '22px' }} />
							)}
							<div className='nav-main-label'>{child.label}</div>
						</div>
					);
				}
				return (
					<>
						<div
							className={
								this.state.selectedNavItem === child.label
									? 'nav-main-container-with-child highlight'
									: 'nav-main-container-with-child'
							}
							onClick={this.handleNavState(child)}
						>
							<div className='nav-main-block'>
								{child.imgUrl === '' ? (
									//<img src={child.imgUrl} className='nav-img' />
									<ExtensionIcon className='nav-img' />
								) : (
									<div style={{ width: '22px' }} />
								)}
								<div className='nav-main-label'>{child.label}</div>
							</div>
							{this.state.selectedNavItem === child.label ? (
								<ExpandMoreIcon className='nav-expand' />
							) : (
								<NavigateNextIcon className='nav-expand' />
							)}
						</div>
						{this.state.selectedNavItem === child.label ? this.handleNavList(child.children) : null}
					</>
				);
			})
		);
	};

	render() {
		return <div className='nav-list-container'>{this.handleNavList(this.state.navOptions)}</div>;
	}
}

export default NavListOption;
