/* eslint-disable jsx-a11y/alt-text */
import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import Badge from '@material-ui/core/Badge';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import AccountCircle from '@material-ui/icons/AccountCircle';
import NotificationsIcon from '@material-ui/icons/Notifications';
import MoreIcon from '@material-ui/icons/MoreVert';
import './index.scss';

const useStyles = makeStyles(theme => ({
	grow: {
		flexGrow: 1
	},
	title: {
		display: 'none',
		[theme.breakpoints.up('sm')]: {
			display: 'block'
		},
		color: 'black',
		opacity: '0.6',
		fontSize: '19px',
		fontWeight: '300'
	},
	sectionDesktop: {
		display: 'none',
		[theme.breakpoints.up('md')]: {
			display: 'flex'
		}
	},
	sectionMobile: {
		display: 'flex',
		[theme.breakpoints.up('md')]: {
			display: 'none'
		}
	},
	appBarColor: {
		backgroundColor: 'white',
		boxShadow: '0px 2px 4px -1px rgba(0,0,0,0), 0px 4px 5px 0px rgba(0,0,0,0.1), 0px 1px 0px 0px rgba(0,0,0,0.1)'
	}
}));

export default class Header extends Component {
	state = {
		anchorEl: null,
		mobileMoreAnchorEl: null
	};

	isMobileMenuOpen = this.state.mobileMoreAnchorEl;

	handleProfileMenuOpen = event => {
		this.setState({ anchorEl: event.currentTarget });
	};

	handleMobileMenuClose = () => {
		this.setState({ mobileMoreAnchorEl: null });
	};

	handleMenuClose = () => {
		this.setState({ anchorEl: null });
		this.handleMobileMenuClose();
	};

	handleMobileMenuOpen = event => {
		this.setState({ mobileMoreAnchorEl: event.currentTarget });
	};
	renderMenu = () => {
		isMenuOpen = this.state.anchorEl;
		return (
			<Menu
				anchorEl={anchorEl}
				anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
				id={'primary-search-account-menu'}
				keepMounted
				transformOrigin={{ vertical: 'top', horizontal: 'right' }}
				open={isMenuOpen}
				onClose={this.handleMenuClose}
			>
				<MenuItem onClick={this.handleMenuClose}>Edit Profile</MenuItem>
				<MenuItem onClick={this.handleMenuClose}>Logout</MenuItem>
			</Menu>
		);
	};

	renderMobileMenu = (
		<Menu
			anchorEl={mobileMoreAnchorEl}
			anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
			id={'primary-search-account-menu-mobile'}
			keepMounted
			transformOrigin={{ vertical: 'top', horizontal: 'right' }}
			open={isMobileMenuOpen}
			onClose={this.handleMobileMenuClose}
		>
			<MenuItem>
				<IconButton aria-label='show 11 new notifications' color='inherit'>
					<Badge badgeContent={11} color='secondary'>
						<NotificationsIcon />
					</Badge>
				</IconButton>
				<p>Notifications</p>
			</MenuItem>
			<MenuItem onClick={this.handleProfileMenuOpen}>
				<IconButton
					aria-label='account of current user'
					aria-controls='primary-search-account-menu'
					aria-haspopup='true'
					color='inherit'
				>
					<AccountCircle />
				</IconButton>
				<p>Profile</p>
			</MenuItem>
		</Menu>
	);
	render() {
		const classes = useStyles();
		return (
			<div className={classes.grow}>
				<AppBar position='static' className={classes.appBarColor}>
					<Toolbar>
						<img
							src='https://i1.wp.com/thehackerstreet.com/wp-content/uploads/2019/09/logo.png?fit=672%2C151&ssl=1'
							className='hs-img'
						/>
						<div className={classes.grow} />
						<Typography className={classes.title} noWrap>
							LEAD DASHBOARD
						</Typography>

						<div className={classes.grow} />
						<div className={classes.sectionDesktop}>
							{/* here full screen img */}
							<IconButton aria-label='show 17 new notifications' className={classes.iconColor}>
								<Badge badgeContent={17} color='secondary'>
									<NotificationsIcon />
								</Badge>
							</IconButton>
							<IconButton
								edge='end'
								aria-label='account of current user'
								aria-controls={'primary-search-account-menu'}
								aria-haspopup='true'
								onClick={this.handleProfileMenuOpen}
							>
								<AccountCircle />
							</IconButton>
						</div>
						<div className={classes.sectionMobile}>
							<IconButton
								aria-label='show more'
								aria-controls={'primary-search-account-menu-mobile'}
								aria-haspopup='true'
								onClick={this.handleMobileMenuOpen}
							>
								<MoreIcon />
							</IconButton>
						</div>
					</Toolbar>
				</AppBar>
				{renderMobileMenu}
				{renderMenu}
			</div>
		);
	}
}
