/* eslint-disable jsx-a11y/alt-text */
import React, { Component } from 'react';
import './index.scss';
import CancelPresentationIcon from '@material-ui/icons/CancelPresentation';

class InfoPanelSideBar extends Component {
	closePanel = () => {
		this.props.closeInfoPanel();
	};
	render() {
		return (
			<div className={this.props.open ? 'info-panel-container' : 'info-panel-container ispanel-close'}>
				<div className='info-panel-close'>
					<CancelPresentationIcon className='close-icon' onClick={this.closePanel} />
					<div>{this.props.infoPanel}</div>
				</div>
			</div>
		);
	}
}

export default InfoPanelSideBar;
