/* eslint-disable jsx-a11y/alt-text */
import React, { Component } from 'react';
import './index.scss';
import hsImg from '../../../assets/logo-icon.svg';
import NavListOption from './NavListOption';

class SideBar extends Component {
	close = () => {
		console.log(this.props.isOpen);
	};
	componentDidMount() {
		document.addEventListener('mousedown', e => this.handleClickOutside(e));
	}
	handleClickOutside(e) {
		if (this.node && !this.node.contains(e.target)) {
			this.props.closeSideBar();
		}
	}
	render() {
		return (
			<nav
				className={this.props.isOpen ? 'side-bar-container side-bar-open' : 'side-bar-container'}
				ref={node => {
					this.node = node;
				}}
			>
				<div className='space-container-img'>
					<img src={hsImg} className={'hs-label-with-img'} />
					<span className='space-label'>Space OS</span>
				</div>
				<NavListOption />
			</nav>
		);
	}
}

export default SideBar;
